export interface User {
    id: string;
    name: string;
    email: string;
    selectedAestheticId?: string;
    createdAt?: string;
}

export interface UserProfile {
    id?: string;
    userId: string;
    phoneNumber?: string;
    address?: Address;
    preferences?: Record<string, unknown>;
    savedAesthetics?: string[];
}

export interface Address {
    street: string;
    city: string;
    state: string;
    country: string;
    postalCode: string;
}

export interface RegisterDto {
    name: string;
    email: string;
    password: string;
}

export interface LoginDto {
    email: string;
    password: string;
}

export interface UpdateUserDto {
    name?: string;
    email?: string;
}

export interface LoginResponse {
    user: User;
    token: string;
}

export interface ApiError {
    error: string;
    errors?: Array<{ msg: string; param: string }>;
}